mapper.select:

select 

from turntable_activity_reward
where activityId = #{activityid,jdbcType=INTEGER}
  and rewardId = #{rewardid,jdbcType=INTEGER}
  and rewardDate = #{rewarddate,jdbcType=TIMESTAMP}

mapper.delete:

delete from turntable_activity_reward
where activityId = #{activityid,jdbcType=INTEGER}
  and rewardId = #{rewardid,jdbcType=INTEGER}
  and rewardDate = #{rewarddate,jdbcType=TIMESTAMP}

mapper.update:

update turntable_activity_reward
set rewardName = #{rewardname,jdbcType=VARCHAR},
  rewardCount = #{rewardcount,jdbcType=INTEGER},
  rewardLeftCount = #{rewardleftcount,jdbcType=INTEGER},
  rewardRatio = #{rewardratio,jdbcType=INTEGER},
  isBroadcast = #{isbroadcast,jdbcType=INTEGER},
  isReward = #{isreward,jdbcType=INTEGER},
  isDiamond = #{isdiamond,jdbcType=INTEGER}
where activityId = #{activityid,jdbcType=INTEGER}
  and rewardId = #{rewardid,jdbcType=INTEGER}
  and rewardDate = #{rewarddate,jdbcType=TIMESTAMP}
